import React,{useState} from 'react'
import PropTypes from 'prop-types'
import Axios from 'axios'
import { useForm } from "react-hook-form";
const Add = props => {
  const {register, handleSubmit, errors}=useForm();
  const Them=data=>{
    const url = "https://5ea1c7f7b9f5ca00166c2226.mockapi.io/conga/sanpham";
    Axios.post(url,data).then(res=>{
    }
    )
  }
  return (
        <div>
<div className="content-wrapper">
        {/* Content Header (Page header) */}
        {/* Main content */}
        <section className="content">
            <div>Thêm sản phẩm</div>
          <div className="box row">
            <div className="col-md-6">
              <form onSubmit={handleSubmit(Them)} encType="multipart/form-data">
              {pro.map(pro=>{
    if(pro.id==id){
        return(
          <div>
                <div className="form-group">
                  <label htmlFor="name">name</label>
                  <input id="name" type="text" name="name" placeholder="Nhập tên " className="form-control"  ref={register({required:true, minLength:4,maxLength:10})} />
                {errors.name && errors.name.type=== "required" && (<div>tên không được để trống </div>)}
                {errors.name && errors.name.type==="minLength" && (<div >tên ít nhất 4 ký tự </div>)}
                {errors.name && errors.name.type==="maxLength" &&  (<div>tên nhiều nhất 10 ký tự </div>)}
                </div>
                <div className="form-group">
                  <label htmlFor="short_detail">image</label>
                  <div>
                    <input className="form-control" id="image" placeholder="Nhập ảnh" name="image" ref={register({required:true})} />
                    {errors.image && <div >Bạn chưa nhập ảnh </div>}

                  </div>
                </div>
                <div className="form-group">
                  <label htmlFor="short_detail">price</label>
                  <div>
                    <input className="form-control" id="price" placeholder="Nhập giá" name="price" ref={register({required:true,min:0})} />
                {errors.price && errors.price.type==="required"&&(<div>chưa nhập giá  </div>)}
                {errors.price && errors.price.type==="min"&&(<div>giá phải lớn hơn 0 </div>)}

                  </div>
                </div>
                <div className="form-group">
                  <label htmlFor="short_detail">sale_price</label>
                  <div>
                    <input className="form-control" id="sale_price" placeholder="Nhập giá giảm" name="sale_price"  ref={register({required:true, number:true})} />
                    {errors.sale_price && errors.sale_price.type==="required" && (<div >chưa nhập giá giảm  </div>)}
                    {errors.sale_price && errors.sale_price.type==="number"&&(<div >giá giảm phải là số </div>)}
                  </div>
                </div>
                <div className="form-group">
                  <label htmlFor="short_detail">desc</label>
                  <div>
                    <input type="password" className="form-control" id="desc" placeholder="nhập mô tả" name="desc" ref={register({required:true,minLength:5})} />
                    {errors.desc && errors.desc.type==="required" && (<div >chưa nhập mô tả</div>)}
                    {errors.desc && errors.desc.type==="minLength" && (<div >ít nhất phải là 5 ký tự</div>)}


                  </div>   
                </div>
                <div className="form-group">
                  <label htmlFor="short_detail">title</label>
                  <div>
                    <input type="password" className="form-control" id="title" placeholder="nhập title" name="title" ref={register({required:true,minLength:5})}  />
                    {errors.title && errors.title.type==="required" && (<div >chưa nhập title</div>)}
                    {errors.title && errors.title.type==="minLength" && (<div >ít nhất phải là 5 ký tự</div>)}
                  </div>
                </div>
                <div className="text-center">
                  <button type="submit" className="btn btn-sm btn-primary" name="submit">Lưu</button>
                  <button type="reset" value="Reset" className="btn btn-sm btn-primary">Hủy</button>
                </div>
                </div>
)}})}
              </form>
            </div>
          </div></section>
        {/* /.content */}
      </div>        </div>
    )
}
export default Add
