import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types'
const Details = (props) => {
let id=parseInt(props.match.params.id);
const [pro, setPro] = useState([]);
useEffect(() => {
  fetch('https://5ea1c7f7b9f5ca00166c2226.mockapi.io/conga/sanpham')
    .then(res => res.json())
    .then((data) => {
      setPro(data);
    })
}, []);
return(
    <div>
{pro.map(pro=>{
    if(pro.id==id){
        <div>
     <div>{pro.id}</div>
    <div>{pro.name}</div>   
     <div>{pro.image}</div> 
     </div>  
    }
})}
</div>
)
}

export default Details
