import React from "react"
import ProtoTypes from "prop-types"
Pagination.ProtoTypes={
    Pagination:ProtoTypes.object.isRequired,
    onPageChange: ProtoTypes.func,
};
Pagination.defaultProps={
    onPageChange:null,
}
function Pagination(){
    const{Pagination,onPageChange}=props;
    const{_page,_limit,_totalRows}=pagination;
    const totalPage= Math.ceil(_totalRows/_limit);

    function handelPageChange(){
        if(onPageChange){
            onPageChange(newpage);
        }
    }
    return(
        <div>
            <button disabled={_page<=1}
              onClick={()=>handelPageChange(_page-1)}>prev</button>
               <button disabled={_page<=totalPage}
              onClick={()=>handelPageChange(_page+1)}>next</button> 
        </div>)
}
export default Pagination
