import React, { useState, useEffect } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import Routers from './routers'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
// import AddProduct from './components/AddProduct';
function App() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading]=useState(false);
    const [currentPage, setCurrentPage]=useState(1);
    const [postPerPage, setPostPerPage]=useState(6);
  useEffect(() => {
    fetch('https://5ea1c7f7b9f5ca00166c2226.mockapi.io/conga/sanpham')
      .then(res => res.json())
      .then((data) => {
        setProducts(data);
      })
  }, []);
  const indexOfLastPost=currentPage*postPerPage;
  const indexOfFisrtPost=indexOfLastPost-postPerPage;
  const currentPost=products.sclice(indexOfFisrtPost,indexOfLastPost);

  const [status, setStatus] = useState(false);
  const [color, setColor] = useState('green');
  const onHandleClick = () => {
    // setStatus(true);
    setColor('red');
  }
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }
  const onHanleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }
  return (
    <div className="App">
      <Routers products={currentPost} loading={loading} onRemove={onHandleRemove} />
    </div>
  )

}
export default App;
